mapper.select:
SELECT I.ORDERMONTH chartLabel, NVL(SUM(D.MENUQTY*M.MENUPRICE),0) chartCount FROM (SELECT TO_CHAR(TRUNC(TO_DATE(#{baseDay},'YYYYMMDD'),'YY'),'YYMM')+LEVEL-1 ORDERMONTH FROM DUAL CONNECT BY LEVEL < 13) I LEFT JOIN ORDERLIST O ON TO_CHAR(O.ORDERDATE,'YYMM') = I.ORDERMONTH
AND O.FCID = #{fcId}
LEFT JOIN ORDERDETAILLIST D ON O.ORDERNUMBER = D.ORDERNUMBER LEFT JOIN MENUINFO M ON D.MENUINDEX = M.MENUINDEX GROUP BY I.ORDERMONTH ORDER BY I.ORDERMONTH

SELECT I.ORDERTIME chartLabel, SUM(M.MENUPRICE*D.MENUQTY) chartCount FROM (SELECT LEVEL-1 ORDERTIME FROM DUAL CONNECT BY LEVEL <25) I LEFT JOIN ORDERLIST O ON TO_CHAR(O.ORDERDATE,'HH24') = I.ORDERTIME
AND TO_CHAR(O.ORDERDATE, 'YYMMDD') = #{selectDate}
AND FCID=#{fcId}
LEFT JOIN ORDERDETAILLIST D ON O.ORDERNUMBER = D.ORDERNUMBER LEFT JOIN MENUINFO M ON M.MENUINDEX = D.MENUINDEX GROUP BY I.ORDERTIME ORDER BY I.ORDERTIME

SELECT I.ORDERDATE as chartLabel, SUM(M.MENUPRICE*D.MENUQTY) as chartCount FROM (SELECT TO_CHAR(TRUNC(TO_DATE(#{baseDay},'YYYYMMDD'),'MM'),'YYYYMMDD')+LEVEL-1 ORDERDATE FROM DUAL CONNECT BY LEVEL < TO_NUMBER(TO_CHAR(LAST_DAY(TO_DATE(#{baseDay},'YYYYMMDD')),'DD'))+1) I LEFT JOIN ORDERLIST O ON TO_CHAR(O.ORDERDATE,'YYYYMMDD') = I.ORDERDATE
AND FCID = #{fcId}
LEFT JOIN ORDERDETAILLIST D ON O.ORDERNUMBER = D.ORDERNUMBER LEFT JOIN MENUINFO M ON M.MENUINDEX = D.MENUINDEX GROUP BY I.ORDERDATE ORDER BY I.ORDERDATE

SELECT * FROM FCCLIENTREG WHERE 1=1

	AND FCID = #{fcId}


	AND CLIENTPHONE = #{clientPhone}


	AND SEQ = #{seq}

SELECT * FROM FCCLIENTREG WHERE FCID = #{vo.fcId}

ORDER BY SEQ DESC
OFFSET #{cri.sno}-1 ROWS FETCH NEXT #{cri.rowsPerPage} ROWS ONLY

SELECT to_char(i.orderdate, 'MM/DD') as chartLabel, SUM(d.menuqty * m.menuprice) as chartCount FROM (SELECT TRUNC(SYSDATE-6 + LEVEL -1) orderdate FROM dual CONNECT BY LEVEL < 8) i LEFT JOIN orderlist o ON o.completeyn = 'Y' AND to_char(o.orderdate,'yymmdd') = to_char(i.orderdate,'yymmdd')
AND o.fcid = #{value}
LEFT JOIN orderdetaillist d ON o.ordernumber = d.ordernumber LEFT JOIN menuinfo m ON m.menuindex = d.menuindex GROUP BY i.orderdate ORDER BY i.orderdate

mapper.insert:
UPDATE franchise 
SET fcClose= 'Y'
WHERE fcId = #{fcId}

UPDATE franchise 
SET FCPASSWORD = #{fcPassword} 
WHERE fcId = #{fcId}

UPDATE franchise 
SET fcName=#{fcName}, fcaddress= #{fcAddress}, fcPhone=#{fcPhone},hoid=#{hoId} 
WHERE fcId = #{fcId}
